import { useState } from "react";
import { Input } from "../signup/Input";
import { client } from "../api/api";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../context/DummyContext";
import { useContext } from "react";

const LoginForm = ({ admin }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const endpoint = admin ? "" : "/alumni/auth";
    const msg = useContext(UserContext);
    console.log("from context: ", msg);

    const navigate = useNavigate();
    const handleSubmit = (e) => {
        e.preventDefault();
        const postCredentials = async () => {
            const res = await client.post(endpoint, {
                username: username,
                password: password,
            });

            if (res.data.username) {
                //Update the context payload to redirect the page
                console.log("authorized");
                navigate("/alumni");
            } else {
                console.log(res.data);
                alert(res.data.message);
            }

            setUsername("");
            setPassword("");
        };

        postCredentials();
    };

    // const redirectToRegist =

    return (
        <form className="bg-white w-1/2 px-6 py-6 font-poppins flex flex-col justify-center gap-5">
            <h1 className="text-4xl">Log In</h1>
            <div className="flex flex-col gap-3">
                <Input
                    label="Username"
                    value={username}
                    handleOnChange={(e) => {
                        setUsername(e.target.value);
                    }}
                />
                <Input
                    label="Password"
                    value={password}
                    type={"password"}
                    handleOnChange={(e) => {
                        setPassword(e.target.value);
                    }}
                />
            </div>
            <button
                type="submit"
                className="w-40 py-2 self-center rounded-md bg-green text-white"
                onClick={handleSubmit}
            >
                Log In
            </button>
        </form>
    );
};

export { LoginForm };
