import { TextInput } from "./TextInput";
import { useState, useContext } from "react";
import { client } from "../../api/api";
import AuthContext from "../../context/AuthContext";

const EditProfile = ({ user }) => {
    const [email, setEmail] = useState(user.email);
    const [phone, setPhone] = useState(user.phone);
    const [cellphone, setCellphone] = useState(user.cellphone);
    const [houseNumber, setHouseNumber] = useState(user.address.houseNumber);
    const [building, setBuilding] = useState(user.address.building);
    const [street, setStreet] = useState(user.address.street);
    const [city, setCity] = useState(user.address.city);
    const [province, setProvince] = useState(user.address.province);
    const [country, setCountry] = useState(user.address.country);
    const { auth } = useContext(AuthContext);

    const postEditData = async () => {
        console.log("api called");
        const res = await client.post(
            "/alumni/edit",
            {
                avatar: "",
                houseNumber: houseNumber,
                building: building,
                street: street,
                city: city,
                province: province,
                country: country,
                phone: phone,
                cellphone: cellphone,
                email: email,
            },
            {
                headers: auth.token,
            }
        );
        console.log("response: ", res);
        console.log("done..");
        if (res) {
            alert("Saved!");
        }
    };

    const submit = (e) => {
        e.preventDefault();
        console.log("submitted");
        // if (
        //     !email &&
        //     !phone &&
        //     !houseNumber &&
        //     !building &&
        //     !street &&
        //     !city &&
        //     !province
        // ) {
        //     alert(" Atleast one input must have.");
        //     return;
        // }

        postEditData();
    };

    return (
        <>
            <form className="relative inset-0 mx-auto z-50 mx-auto my-20 w-112 bg-grey-100 px-5 py-5 font-poppins flex flex-col border border-grey-200 shadow-sm shadow-grey-400">
                <p className="mb-2 text-2xl text-grey mx-auto">Edit Profile</p>
                <div className="my-3 self-center text-center">
                    <div className="w-32 h-32 bg-black text-white"></div>
                    <p className="text-blue">Change Avatar</p>
                </div>
                <div className="my-2 flex flex-col gap-3">
                    <TextInput
                        value={email}
                        label={"Email"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setEmail(e.target.value);
                        }}
                    />
                    <TextInput
                        value={cellphone}
                        label={"Cellphone"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setCellphone(e.target.value);
                        }}
                    />
                    <TextInput
                        value={phone}
                        label={"Phone"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setPhone(e.target.value);
                        }}
                    />
                    <TextInput
                        value={houseNumber}
                        label={"House Number"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setHouseNumber(e.target.value);
                        }}
                    />
                    <TextInput
                        value={building}
                        label={"Building"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setBuilding(e.target.value);
                        }}
                    />
                    <TextInput
                        value={street}
                        label={"Street"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setStreet(e.target.value);
                        }}
                    />
                    <TextInput
                        value={city}
                        label={"City"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setCity(e.target.value);
                        }}
                    />
                    <TextInput
                        value={province}
                        label={"Province"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setProvince(e.target.value);
                        }}
                    />
                    <TextInput
                        value={country}
                        label={"Country"}
                        type={"text"}
                        handleChange={(e) => {
                            console.log("triggered");
                            setCountry(e.target.value);
                        }}
                    />
                    <button
                        className="bg-blue text-white w-24 py-1 font-poppins text-sm rounded self-center"
                        onClick={submit}
                    >
                        Save
                    </button>
                </div>
            </form>
        </>
    );
};

export { EditProfile };
